import React from 'react'
import { useRef,useEffect,useState} from 'react';
import '../Styles/style_home.css'
import '../Components/HideNav'
import dronevideo from '../Assets/dronevid.mp4'

function Home() {
  const product = [
    {
      name: "Rice",
      soiltype: ["Sandy"],
      temprange: ["Tropical"],
      wateravail: ["Abundant"],
    },
    {
      name: "Potatoes",
      soiltype: ["Clay"],
      temprange: ["Cold"],
      wateravail: ["Adequate"],
    },
    {
      name: "Corn (Maize)",
      soiltype: ["Loam"],
      temprange: ["Temperate"],
      wateravail: ["Seasonal"],
    }
  ]

  const videoRef = useRef(null);
  useEffect(() => {
    videoRef.current.play();
  }, []);

  const [selectedOptioncase1, setSelectedOptioncase1] = useState('');
  const handleSelectChangecase1 = (event) => {
    setSelectedOptioncase1(event.target.value);
  };
  const [selectedOptioncase2, setSelectedOptioncase2] = useState('');
  const handleSelectChangecase2 = (event) => {
    setSelectedOptioncase2(event.target.value);
  };
  const [selectedOptioncase3, setSelectedOptioncase3] = useState('');
  const handleSelectChangecase3 = (event) => {
    setSelectedOptioncase3(event.target.value);
  };

  const [selectedCrop, setSelectedCrop] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const matchingCrop = product.find(crop =>
      crop.soiltype.includes(selectedOptioncase1) &&
      crop.temprange.includes(selectedOptioncase2) &&
      crop.wateravail.includes(selectedOptioncase3)
    );
    
    if (matchingCrop) {
      setSelectedCrop(matchingCrop.name);
    } else {
      setSelectedCrop("No matching crop found.");
    }
  };

  return (
    <div className="homepage">
      <div className="box1">
        <video ref={videoRef} className="video" autoPlay muted loop>
          <source src={dronevideo} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        <b>
          <p>AgriCity's agricultural drones are equipped with cutting-edge sensors and imaging technology,<br /> offering farmers comprehensive aerial insights for crop monitoring and management. Our drones enable precise <br /> spraying, mapping, and analysis, empowering farmers to make data-driven decisions and optimize yields.<br /> With user-friendly interfaces and robust performance, Agricity drones streamline farming operations,<br /> reduce input costs, and promote sustainable practices, ensuring a prosperous future for agriculture.<br /></p>
        </b>
      </div>
      <div className="box2">
    </div>
    </div>
  )
}

export default Home
